// }

class App extends Component {

    constructor(props) {
      super(props);

      this.state = {
        items: [
          {
            title: "Чай",
            taken: false
          },
          {
            title: "Сахар",
            taken: true
          },
          {
            title: "Колбаса",
            taken: false
          }
        ]
      }
    }

    toggleItem(index){
      // console.log(index);
      const items = this.state.items;
      let taken = items[index].taken;
      items[index].taken = !taken;

      this.setState({items});
    }

    addItem = async (e) => {
      //создать текстовое поле и кнопку
      //записать текущее состояние в переменную
      //изменить сосотояние
      //сохранить новое состояние
      e.preventDefault();
      var newItem = e.target.elements.product.value;
      this.state.items.push(newItem);
      this.setState({});
    }

    deleteItem(){

    }

    render() {
      return (
        <div>
          <Form addNewItem={this.addItem}/>
          <ol>
            {
                this.state.items.map((item, index) => {
                // return <li key={index}>{items.title}</li>
                const className = item.taken ? 'taken' : '';
                return <ProductItem   key = {index}
                                      title = {item.title}
                                      className = {className}
                                      toggleItem = {this.toggleItem.bind(this, index)}
                />
            })
            }
          </ol>          
        </div>

      );
    }
}

export default App;
